mapper.select:
SELECT pushId, pushMsg, updateTime, isUse, isDel FROM ( SELECT push_id AS pushId, push_msg AS pushMsg, update_time AS updateTime, is_use as isUse, is_del as isDel FROM m_app_push_log WHERE device_id = #{deviceId} AND push_type = 0 and push_model=1
AND update_time>#{pushMsgTime}
AND is_use=0 and is_del=0
UNION ALL
SELECT push_id AS pushId, push_msg AS pushMsg, update_time AS updateTime, is_use as isUse, is_del as isDel FROM m_app_push_log WHERE push_type = 1 and push_model=1
AND update_time>#{pushMsgTime}
AND is_use=0 and is_del=0
AND find_in_set( #{tagName},tag_name )
UNION ALL
SELECT push_id AS pushId, push_msg AS pushMsg, update_time AS updateTime, is_use as isUse, is_del as isDel FROM m_app_push_log WHERE push_type = 2 and push_model=1
AND update_time>#{pushMsgTime}
AND is_use=0 and is_del=0
) a ORDER BY a.updateTime ASC

mapper.update:
UPDATE
${table}
SET is_success=#{isSuccess} , create_time=NOW()
WHERE push_id=#{pushId} and device_id=#{deviceId}

mapper.select:
SELECT device_id as deviceId
from m_user
where 1=1 and customized=#{biaoshi}

    and o_id=#{tagName}

mapper.update:
UPDATE m_user
SET is_activation=0
where user_id=#{userId}

UPDATE m_app_push_user_tag
SET is_success=#{isSuccess}
where
registration_id=#{registrationId} and device_id=#{deviceId}

update m_app_push_log
set is_push_seccess=#{isPushSeccess}

    ,msg_id=#{msgId}

where push_id=#{pushId}

update m_app_push_registration_id
set registration_id=#{registrationId}
where device_id=#{deviceId}
